    private void LOGIN_MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_LOGIN_MouseClicked
    /* WHEN THE PHOTO OR IMAGE IS CLICKED AND IF USERNAME AND PASSWORD IS
       CORRECT THEN ADMIN PAGE WILL BE DISPLAYED. */
    if (           
           
           User_Id.getText().equals("peperoni") && 
           Password.getText().equals("pizzeria")
        )
           {   
                Admin_Page ap=new Admin_Page();
                ap.setVisible(true);
                this.dispose();
           }
    else 
    JOptionPane.showMessageDialog(rootPane,"USERNAME/PASSOWORD IS INCORRECT");        
    }//GEN-LAST:event_LOGIN_MouseClicked

