    private void HOME_MouseClicked(java.awt.event.MouseEvent evt) {//GEN-FIRST:event_HOME_MouseClicked
    // WHEN PEPERONI PIZZERIA LOGO IS CLICKED HOME FRAME WILL BE DISPLAYED.
       Home hp=new Home();
       hp.setVisible(true);
       this.dispose();
    }//GEN-LAST:event_HOME_MouseClicked

